function evaluateRPN(expression) {
  const stack = [];
  const tokens = expression.split(' ');

  tokens.forEach(token => {
    if (!isNaN(token)) {
      stack.push(Number(token));
    } else {
      const b = stack.pop();
      const a = stack.pop();
      switch (token) {
        case '+':
          stack.push(a + b);
          break;
        case '-':
          stack.push(a - b);
          break;
        case '*':
          stack.push(a * b);
          break;
        case '/':
          stack.push(Math.trunc(a / b)); 
          break;
        default:
          throw new Error('Неизвестный оператор: ' + token);
      }
    }
  });

  return stack.pop();
}

module.exports = evaluateRPN;